package javaProject;

public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// program 1
		 int q3= 5;
		   while(q3 >= 1) {
			   System.out.println(q3);
			   q3 --;
		   }
		   
		   
		 // program 2
		   
		   int q1=1;
		   while(q1<=5) {
			   System.out.println(q1);
			   q1++;
		   }
		   
		   // program 3
		   
		   int q2=1;
		   while(q2<=5) {
			   System.out.println("Hello");
			   q2++;
		   }
		   
		   // program 4
		   // table of 2
		   int q4=2;
		   while(q4<=20) {
			  System.out.println(q4);
			  q4=q4+2;
		   }
		   
		   
		   // break
		   int q5=2;
		   while(q5<=20) {
			   if(q5==6) {
				   break;
			   }
			  System.out.println(q5);
			  q5=q5+2;
		   }
		   
		   // continue
		   
		   int q6=1;
		   while(q6<=5) {
			   if(q6==2) {
				   q6++;
				   continue;
			   }
			   System.out.println(q6);
			   q6=q6+1;
		   }
		   
		   
		   
		   
		   
		   
		   
		   
		   
		   
		   
		   
		   
		   
		   
		   

	}

}
